import numpy as np
from scipy.special import comb
from matplotlib import pyplot as plt
from sklearn.model_selection import KFold
from sklearn.svm import LinearSVC

stuff=np.load("data_real.npz")
X_trn = stuff['X_trn']
Y_trn = stuff['Y_trn']
X_tst = stuff['X_tst']

# print(X_trn.shape)
# print(Y_trn)


# In[2]:


def compute_hinge_loss_binary(y_pred, y_test):
    hinge_losses = []

    for i in range(len(y_pred)):
        y = y_test[i]
        f = y_pred[i]
        hinge_loss = max(0, 1 - y * f)
        hinge_losses.append(hinge_loss)

    return np.mean(hinge_losses)


# In[5]:


# Question 14
lamda = [2, 20, 200]
max_iter = 10000
hinge_loss_result = []
for λ in lamda:
    kf = KFold(n_splits=5)
    hinge_losses = []
    for train_index, test_index in kf.split(X_trn):
        X_train, X_test = X_trn[train_index], X_trn[test_index]
        y_train, y_test = Y_trn[train_index], Y_trn[test_index]
        clf = LinearSVC(
            loss='hinge',
            tol=1e-4,
            C= 1/(2*λ),
            max_iter=max_iter
        )
        clf.fit(X_train, y_train)
        y_pred = clf.decision_function(X_test)
        # print("y_pred:", y_pred)
        # print("y_test:", y_test)
        hinge_losses.append(compute_hinge_loss_binary(y_pred, y_test))

    hinge_loss_result.append(np.mean(hinge_losses))
print(hinge_loss_result)


